#include <bits/stdc++.h>using namespace std; typedef long long ll;const ll INF = 1e18; void solve() {    int n, m, k;    cin >> n >> m >> k;     vector<vector<pair<int, int>>> adj(n);    for (int i = 0; i < m; i++) {        int u, v, w;        cin >> u >> v >> w;        u--; v--;        adj[u].push_back({v, w});        adj[v].push_back({u, w});    }     // We only care about the shortest train route to each city initially    vector<ll> min_train(n, INF);    ll deleted_duplicates = 0;    for (int i = 0; i < k; i++) {        int u, w;        cin >> u >> w;        u--;        if (w < min_train[u]) {            if (min_train[u] != INF) deleted_duplicates++;             min_train[u] = w;        } else {            deleted_duplicates++;        }    }     // Dijkstra    vector<ll> d(n, INF);    priority_queue<pair<ll, int>, vector<pair<ll, int>>, greater<pair<ll, int>>> pq;     d[0] = 0;    pq.push({0, 0});     // Treat trains as potential starting distances    for (int i = 1; i < n; i++) {        if (min_train[i] != INF) {            d[i] = min_train[i];            pq.push({d[i], i});        }    }     while (!pq.empty()) {        ll dist = pq.top().first;        int u = pq.top().second;        pq.pop();         if (dist > d[u]) continue;         for (auto &edge : adj[u]) {            int v = edge.first;            int w = edge.second;            if (d[u] + w < d[v]) {                d[v] = d[u] + w;                pq.push({d[v], v});            }        }    }     // Now count road in-degrees for the Shortest Path DAG    // A train to city 'i' is only necessary if NO road can reach it in d[i]    ll useful_trains = 0;    for (int i = 1; i < n; i++) {        if (min_train[i] == INF) continue;         bool can_reach_by_road = false;        // Check all road edges (u, i)        for (auto &edge : adj[i]) {            int u = edge.first;            int w = edge.second;            // Shortest Path Condition            if (d[u] + w == d[i]) {                can_reach_by_road = true;                break;            }        }         // If road is shorter or equal, the train is NOT useful        if (min_train[i] < d[i]) {            // This case shouldn't happen with correct Dijkstra,             // but conceptually this train would be useless        } else if (min_train[i] == d[i] && !can_reach_by_road) {            useful_trains++;        }    }     cout << k - useful_trains << endl;} int main() {    ios::sync_with_stdio(false);    cin.tie(nullptr);    solve();    return 0;}